package com.Basicblz;

public class PrimeNumBlz {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int n = 1;
		for (int i = 1; i <= n / 2; i++) {

			if (n % 2 == 0) {

				System.out.println("It  not is prime");
			} else {

				System.out.println("It is  prime");
			}

		}

	}

}
