package com.Basicblz;

public class PrimeNumIfElseBlz {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n = 3;
		int pri = n / 2;

		if (pri % 2 == 0) {

			System.out.println("It  not is prime");
		} else {

			System.out.println("It is  prime");
		}

	}

}
